mapper.delete:
DELETE FROM t_category
WHERE author_id = #{authorId,jdbcType=INTEGER}
AND parent_id = #{parentId,jdbcType=INTEGER}

mapper.update:
UPDATE t_category
SET parent_id = #{parentId,jdbcType=INTEGER},name=#{name,jdbcType=VARCHAR}
WHERE id = #{id,jdbcType=INTEGER}
AND author_id = #{authorId,jdbcType=INTEGER}

mapper.select:
SELECT * FROM t_category
WHERE author_id = #{authorId,jdbcType=INTEGER}

    and parent_id = #{parentId,jdbcType=INTEGER}

ORDER BY  id

	SELECT * FROM t_category
    WHERE author_id = #{authorId,jdbcType=INTEGER}
        and name = #{name,jdbcType=INTEGER}
    limit 1

SELECT t1.*,IF(count(*)=1,0,count(*)) as article_counts
	FROM t_category t1
	left join t_article t2
	on t1.id = t2.category_ids

		and t2.status = #{articleStatus,jdbcType=INTEGER}

    WHERE t1.author_id = #{authorId,jdbcType=INTEGER}

        and t1.parent_id = #{parentId,jdbcType=INTEGER}

    group by t1.id
    ORDER BY article_counts desc
